import { connect } from 'react-redux'
import {reset} from 'redux-form'
import {insertClient, getClientIds} from "../../component/editdata/rest";
import {createInsertClientStatusAction, createGetClientIdsAction} from "../../action/editdata/aditDataActions"
import AddClientPage from "../../component/editdata/AddClientPage";

const mapResult = (dispatch,actionCreator) => res => dispatch(actionCreator(res));
const resetForm = (dispatch,formName) => () => dispatch(reset(formName))

const mapStateToProperties = state => {
    return {
        insertClientResult: state.insertClientResult,
        getClientIdsResult: state.getClientIdsResult
    }
};

const mapPropertiesToState = dispatch => {
    return {
        addClient: clientForm => {
            insertClient(clientForm)(mapResult(dispatch,createInsertClientStatusAction), resetForm(dispatch, 'addClient'));
        },
        getClientIds: () => {
            getClientIds()(mapResult(dispatch, createGetClientIdsAction))
        }
    }
};

const AddClientPageRedux = connect(mapStateToProperties, mapPropertiesToState)(AddClientPage);

export default AddClientPageRedux
